// MarketingAssign.tsx
import React, { useState, useEffect } from 'react';
import { marketingServiceApi } from './MarketingService';

const MarketingAssign: React.FC = () => {
  const [selectedUser, setSelectedUser] = useState<string>('');
  const [selectedOffers, setSelectedOffers] = useState<string[]>([]);
  const [availableUsers, setAvailableUsers] = useState<string[]>([]);
  // Mock data for available offers (replace with actual data)
  const availableOffers = ['Offer1', 'Offer2', 'Offer3'];

  // Fetch available users (replace with actual user data)
  useEffect(() => {
    const fetchAvailableUsers = async () => {
      // Fetch user data from your API or service
      // For now, using a mock list of users
      const mockUsers = ['John', 'Jane', 'Doe'];
      setAvailableUsers(mockUsers);
    };

    fetchAvailableUsers();
  }, []);

  const handleAssign = () => {
    // Call the marketingServiceApi to assign offers to the selected user
    marketingServiceApi.assignUserOffers(selectedUser, selectedOffers);
    // Reset the form
    setSelectedUser('');
    setSelectedOffers([]);
  };

  return (
    <div>
      <h2>Marketing Assign:</h2>
      <form>
        <label>
          Select User:
          <input
            type="text"
            value={selectedUser}
            onChange={(e) => setSelectedUser(e.target.value)}
            list="usersList" // Connect input to the datalist
          />
          {/* Datalist for typeahead based on available users */}
          <datalist id="usersList">
            {availableUsers.map((user) => (
              <option key={user} value={user} />
            ))}
          </datalist>
        </label>
        <label>
          Select Offers:
          <select
            multiple
            value={selectedOffers}
            onChange={(e) => setSelectedOffers(Array.from(e.target.selectedOptions, (option) => option.value))}
          >
            {availableOffers.map((offer) => (
              <option key={offer} value={offer}>
                {offer}
              </option>
            ))}
          </select>
        </label>
        <button type="button" onClick={handleAssign}>
          Assign Offers
        </button>
      </form>
    </div>
  );
};

export default MarketingAssign;
