// UserAdd.tsx
import React, { useState } from 'react';
import { UserModel } from './UserModel';

const UserAdd: React.FC = () => {
  const initialUser: UserModel = {
    id: '',
    firstname: '',
    lastname: '',
    street: '',
    country: '',
    isMarketingSent: '',
    marketingDateSent: '',
    emailAddress: '',
  };

  const [newUser, setNewUser] = useState<UserModel>(initialUser);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setNewUser((prevUser) => ({ ...prevUser, [name]: value }));
  };

  const handleAddUser = () => {
    // Here, you can perform the actual API call to add the new user
    // For now, let's log the new user data
    console.log('New User:', newUser);
    // You can also reset the form after adding the user if needed
    setNewUser(initialUser);
  };

  return (
    <div>
      <h2>Add User:</h2>
      <form>
        <label>
          First Name:
          <input
            type="text"
            name="firstname"
            value={newUser.firstname}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Last Name:
          <input
            type="text"
            name="lastname"
            value={newUser.lastname}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Street:
          <input
            type="text"
            name="street"
            value={newUser.street}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Country:
          <input
            type="text"
            name="country"
            value={newUser.country}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Is Marketing Sent:
          <input
            type="text"
            name="isMarketingSent"
            value={newUser.isMarketingSent}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Marketing Date Sent:
          <input
            type="text"
            name="marketingDateSent"
            value={newUser.marketingDateSent}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Email Address:
          <input
            type="text"
            name="emailAddress"
            value={newUser.emailAddress}
            onChange={handleInputChange}
          />
        </label>
        <button type="button" onClick={handleAddUser}>
          Add User
        </button>
      </form>
    </div>
  );
};

export default UserAdd;
